pub fn oneline(){vec![Box::new(|v:Vec<Vec<i32>>|v.iter().filter(|v|(v[0]<=v[2]&&v[1]>=v[3])||(v[2]<=v[0]&&v[3]>=v[1])).collect::<Vec<_>>().len())as Box<dyn Fn(Vec<Vec<i32>>)->usize>,Box::new(|v:Vec<_>|v.len()),].iter().for_each(|f|{println!("{}",f(include_str!("source.txt").lines().map(|l|{l.split(['-',',']).map(|v|v.parse::<i32>().unwrap()).collect::<Vec<_>>()}).filter(|v|(v[0]<=v[2]&&v[2]<=v[1])||(v[2]<=v[0]&&v[0]<=v[3])).collect::<Vec<_>>()));})}